<script setup>
  const burgerIsOpen = ref(false);
  const isElementActive = ref([false, false, false, false, false, false]);
  const isAccOpen = ref([false, false, false, false, false]);
  const {openFeedback} = useModal();

  const openModal = () =>{
    openFeedback();
  }



  const handleScroll = () => {
    const scrollPositions = [1380, 1780, 2180, 2780, 3280, 3780];
    const scrollPositionsMobile = [680, 1080, 1480, 1800, 2280, 2680];
    const windowScrollY = window.scrollY;
    if(document.documentElement.clientWidth >= 853){
      for (let i = 0; i < scrollPositions.length; i++) {
        if (windowScrollY > scrollPositions[i]) {
          isElementActive.value[i] = true;
        } else {
          isElementActive.value[i] = false;
        }
      }
    }else{
      for (let i = 0; i < scrollPositionsMobile.length; i++) {
        if (windowScrollY > scrollPositionsMobile[i]) {
          isElementActive.value[i] = true;
        } else {
          isElementActive.value[i] = false;
        }
      }
    }
  };

  const closeAccordion = (accIndex, accValue) => {
    if (accValue) {
      isAccOpen.value.forEach(function(element, index, array){
        if(index == accIndex){
          isAccOpen.value[index] = true;
        }
        else{
          isAccOpen.value[index] = false;
        }
      })
    }
  };
  const showControl = ref(true);
const video = ref(null);

const togglePlayPause = () => {
  if (video.value.paused) {
    video.value.play();
  } else {
    video.value.pause();
  }
};

const onPlay = () => {
  showControl.value = false;
};

const onPause = () => {
  showControl.value = true;
};
  onMounted(() => {
    window.addEventListener('scroll', handleScroll);
    handleScroll();
  });
  onUnmounted(()=>{
    window.removeEventListener('scroll',handleScroll);
  })

</script>

